import React, {useCallback} from 'react';
import {GetStaticPaths, GetStaticProps } from 'next';

import Link from 'next/link';
import Head from 'next/head';

import axios from "axios";
import { Container, Header, Cards } from './styles';

type Props = {
  repositories: {
    id:number;
    name:string;
    description:string;
  }
}| Object[];

